import React, { useState } from 'react';
import axios from 'axios';

const PaymentModal = ({ user, onClose, onPaymentSuccess }) => {
  const [amount, setAmount] = useState(100);
  const [paymentMethod, setPaymentMethod] = useState('CARD');
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!amount || amount <= 0) {
      setError('Please enter a valid amount');
      return;
    }
    
    setLoading(true);
    setError('');
    
    try {
      const response = await axios.post(`/api/users/${user._id}/wallet/add`, {
        amount,
        paymentMethod
      });
      
      if (response.data.success) {
        onPaymentSuccess(response.data.user);
      }
    } catch (error) {
      if (error.response && error.response.data) {
        setError(error.response.data.message);
      } else {
        setError('An error occurred. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h3>Add Money to Wallet</h3>
          <button className="close-btn" onClick={onClose}>&times;</button>
        </div>
        
        {error && <div className="alert alert-danger">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="amount">Amount (₹)</label>
            <input
              type="number"
              id="amount"
              value={amount}
              onChange={(e) => setAmount(Number(e.target.value))}
              min="1"
              disabled={loading}
            />
          </div>
          
          <div className="form-group">
            <label>Payment Method</label>
            <div>
              <div>
                <input
                  type="radio"
                  id="card"
                  name="paymentMethod"
                  value="CARD"
                  checked={paymentMethod === 'CARD'}
                  onChange={() => setPaymentMethod('CARD')}
                  disabled={loading}
                />
                <label htmlFor="card" style={{ display: 'inline', marginLeft: '5px' }}>Credit/Debit Card</label>
              </div>
              
              <div>
                <input
                  type="radio"
                  id="upi"
                  name="paymentMethod"
                  value="UPI"
                  checked={paymentMethod === 'UPI'}
                  onChange={() => setPaymentMethod('UPI')}
                  disabled={loading}
                />
                <label htmlFor="upi" style={{ display: 'inline', marginLeft: '5px' }}>UPI</label>
              </div>
              
              <div>
                <input
                  type="radio"
                  id="netbanking"
                  name="paymentMethod"
                  value="NETBANKING"
                  checked={paymentMethod === 'NETBANKING'}
                  onChange={() => setPaymentMethod('NETBANKING')}
                  disabled={loading}
                />
                <label htmlFor="netbanking" style={{ display: 'inline', marginLeft: '5px' }}>Net Banking</label>
              </div>
            </div>
          </div>
          
          <button
            type="submit"
            className="btn btn-primary btn-block"
            disabled={loading}
          >
            {loading ? 'Processing...' : 'Add Money'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default PaymentModal;
